// Vector-addition.cu
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;
#include &lt;math.h&gt;
#include &lt;assert.h&gt;
#include &lt;time.h&gt;
#include &lt;cuda.h&gt;
#define N 5120
#define MAX_ERR 1e-6
// GPU function that adds two vectors 
__global__ void vector_add(float *a, float *b, float *out, int n) {
int i = blockIdx.x * blockDim.x * blockDim.y + threadIdx.y * blockDim.x + threadIdx.x;   // Allow the   threads only within the size of N
if(i &lt; n)
{
out[i] = a[i] + b[i];
}
// Synchronice all the threads 
__syncthreads();
}
int main()
{
// Initialize the memory on the host
float *a, *b, *out;
// Allocate host memory
a   = (float*)malloc(sizeof(float) * N);
b   = (float*)malloc(sizeof(float) * N);
out = (float*)malloc(sizeof(float) * N);
// Initialize the memory on the device
float *d_a, *d_b, *d_out;
// Allocate device memory
cudaMalloc((void**)&amp;d_a, sizeof(float) * N);
cudaMalloc((void**)&amp;d_b, sizeof(float) * N);
cudaMalloc((void**)&amp;d_out, sizeof(float) * N); // Initialize host arrays
for(int i = 0; i &lt; N; i++)
{
a[i] = 1.0f;
b[i] = 2.0f;
}
// Transfer data from host to device memory
cudaMemcpy(d_a, a, sizeof(float) * N, cudaMemcpyHostToDevice);
cudaMemcpy(d_b, b, sizeof(float) * N, cudaMemcpyHostToDevice);
// Thread organization 
dim3 dimGrid(ceil(N/32), ceil(N/32), 1);
dim3 dimBlock(32, 32, 1); // execute the CUDA kernel function 
vector_add&lt;&lt;&lt;dimGrid, dimBlock&gt;&gt;&gt;(d_a, d_b, d_out, N);
// Transfer data back to host memory
cudaMemcpy(out, d_out, sizeof(float) * N, cudaMemcpyDeviceToHost);
// Verification
for(int i = 0; i &lt; N; i++)
{
assert(fabs(out[i] - a[i] - b[i]) &lt; MAX_ERR);
}
printf("out[0] = %f\n", out[0]);
printf("PASSED\n");
// Deallocate device memory
cudaFree(d_a);
cudaFree(d_b);
cudaFree(d_out);
// Deallocate host memory
free(a); free(b); free(out);
return 0;
}
